from typing import List

class Solution:
    def exist(self, board: List[List[str]], word: str) -> bool:
        if not board:
            return False
        def DFS(depth, x, y, visited:set):
            if x < 0 or y < 0 or x >= len(board) or y >= len(board[0]):
                return False
            if word[depth] != board[x][y] or (x, y) in visited:
                return False
            if depth == len(word) - 1:
                return True
            v_set = visited.copy()
            v_set.add((x, y))
            return DFS(depth+1, x+1, y, v_set) or DFS(depth+1, x, y+1, v_set) or DFS(depth+1, x-1, y, v_set) or DFS(depth+1, x, y-1, v_set)
        for i in range(len(board)):
            for j in range(len(board[0])):
                if DFS(0, i, j, set()):
                    return True
        return False

a = Solution()
